/**
 * @title VulnerableBank
 * @dev ‰∏Ä‰∏™Â≠òÂú®ÈáçÂÖ•ÊºèÊ¥ûÁöÑÁÆÄÂçïÈì∂Ë°åÂêàÁ∫¶ - Polkadot Asset Hub ÁâàÊú¨
 * 
 * ÊºèÊ¥ûËØ¥ÊòéÔºö
 * 1. withdraw ÂáΩÊï∞Âú®ËΩ¨Ë¥¶ÂâçÊ≤°ÊúâÊõ¥Êñ∞‰ΩôÈ¢ù
 * 2. ‰ΩøÁî® call ËøõË°åËΩ¨Ë¥¶ÔºåÂÖÅËÆ∏Êé•Êî∂ËÄÖÊâßË°å‰ª£Á†Å
 * 3. ÊîªÂáªËÄÖÂèØ‰ª•Âú®Êé•Êî∂Âà∞ ETH Êó∂ÂÜçÊ¨°Ë∞ÉÁî® withdraw
 * 
 * ÈÉ®ÁΩ≤Âà∞ Polkadot Asset Hub ÊµãËØïÁΩëËøõË°åÊºîÁ§∫
 */
contract VulnerableBank {
    // Â≠òÂÇ®ÊØè‰∏™Áî®Êà∑ÁöÑ‰ΩôÈ¢ù
    mapping(address => uint256) public balances;
    
    // ‰∫ã‰ª∂ÔºöÂ≠òÊ¨æ
    event Deposit(address indexed user, uint256 amount);
    
    // ‰∫ã‰ª∂ÔºöÂèñÊ¨æ
    event Withdraw(address indexed user, uint256 amount);
    
    /**
     * @dev Â≠òÊ¨æÂáΩÊï∞
     */
    function deposit() public payable {
        require(msg.value > 0, "Deposit amount must be greater than 0");
        balances[msg.sender] += msg.value;
        emit Deposit(msg.sender, msg.value);
    }
    
    /**
     * @dev ÂèñÊ¨æÂáΩÊï∞ - Â≠òÂú®ÈáçÂÖ•ÊºèÊ¥ûÔºÅ
     * 
     * ÊºèÊ¥ûÁÇπÔºö
     * 1. ÂÖàËΩ¨Ë¥¶ÔºàÁ¨¨ 42 Ë°åÔºâ
     * 2. ÂêéÊõ¥Êñ∞‰ΩôÈ¢ùÔºàÁ¨¨ 47 Ë°åÔºâ
     * 3. Ëøô‰∏™È°∫Â∫èÂÖÅËÆ∏ÊîªÂáªËÄÖÂú®‰ΩôÈ¢ùÊõ¥Êñ∞ÂâçÈáçÂ§çË∞ÉÁî®
     */
    function withdraw(uint256 _amount) public {
        require(balances[msg.sender] >= _amount, "Insufficient balance");
        
        // üö® ÊºèÊ¥ûÔºöÂú®Êõ¥Êñ∞‰ΩôÈ¢ù‰πãÂâçËΩ¨Ë¥¶ÔºÅ
        // ‰ΩøÁî® call ÂÖÅËÆ∏Êé•Êî∂ËÄÖÊâßË°å‰ª£Á†ÅÔºàÈáçÂÖ•ÁÇπÔºâ
        (bool success, ) = msg.sender.call{value: _amount}("");
        if (!success) {
            revert("Transfer failed");
        }
        
        // üö® ÊºèÊ¥ûÔºö‰ΩôÈ¢ùÊõ¥Êñ∞Â§™Êôö‰∫ÜÔºÅ
        // ÊîªÂáªËÄÖÂèØ‰ª•Âú®ËøôË°åÊâßË°åÂâçÂ§öÊ¨°Ë∞ÉÁî® withdraw
        // Ê≥®ÊÑèÔºö‰ΩøÁî® unchecked ÊòØ‰∏∫‰∫ÜÊºîÁ§∫ÊºèÊ¥ûÔºåÂÆûÈôÖ‰ª£Á†Å‰∏çÂ∫îËØ•ËøôÊ†∑ÂÅö
        unchecked {
            balances[msg.sender] -= _amount;
        }
        
        emit Withdraw(msg.sender, _amount);
    }
    
    /**
     * @dev Êü•ËØ¢‰ΩôÈ¢ù
     */
    function getBalance(address _user) public view returns (uint256) {
        return balances[_user];
    }
    
    /**
     * @dev Êü•ËØ¢ÂêàÁ∫¶ÊÄª‰ΩôÈ¢ù
     */
    function getContractBalance() public view returns (uint256) {
        return address(this).balance;
    }
}

